package problems_by_year.year_2020.month_05.problems_0518.Find_All_Anagrams_in_a_String;

import java.util.*;

public class Solution {

    public static void main(String[] args) {
        String s = "cbaebabacd";
        String p = "abc";

//        String s = "";
//        String p ="yqrbgjdwtcaxzsnifvhmou";
        System.out.println(s.length());
        Solution solution = new Solution();
        List<Integer> lst = solution.findAnagrams(s, p);
    }

    /**
     * 暴力搜索TLE，可以使用Trie的方法，构建所有短字符串的可能组合，然后用长字符串遍历。
     * @param s
     * @param p
     * @return
     */
    public List<Integer> findAnagrams(String s, String p) {
        int lenP = p.length();
        List<Integer> lst = new ArrayList<>();
        char[] pArr = p.toCharArray();
        Arrays.sort(pArr);
        p = String.valueOf(pArr);
        for (int i = 0; i < s.length(); i++) {
            StringBuilder stringBuilder = new StringBuilder();
            int end = lenP+i < s.length() ? lenP+i : s.length();
            for (int j = i; j < end; j++) {
                char c = s.charAt(j);
                stringBuilder.append(c);
            }
            char[] chars = stringBuilder.toString().toCharArray();
            Arrays.sort(chars);
            if (p.equals(String.valueOf(chars))) {
                lst.add(i);
            }
        }
        return lst;
    }
}

